t = [0:0.01:0.98];y1 = sin(2*pi*4*t);y2 = cos(2*pi*4*t);%close;plot(t, y1)hold on;plot(t, y2, 'r')xlabel('time');ylabel('value');legend('sin', 'cos');%legend(..., "location", northwest);title('My first plot');%print -dpng 'DominiksPlot.png'close;%figure(1), plot(t, y1);%figure(2), plot(t, y2);subplot(1,2,1);plot(t, y1);xlabel('time');ylabel('value');legend('sin');subplot(1,2,2);plot(t, y2, 'r');xlabel('time');ylabel('value');legend('cos');%axis([0.5 1 -1 1])figure;imagesc(magic(15)), colorbar, colormap gray;